package com.twjitm.threads.thread.task;import java.util.concurrent.BlockingQueue;import java.util.concurrent.LinkedBlockingQueue;/** * @author EGLS0807 - [Created on 2018-08-23 17:24] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */public class NettyTaskQueue<V> {    private boolean comple = true;    /**     * 任务队列     */    BlockingQueue<V> tasksQueue = new LinkedBlockingQueue<V>();    /**     * 下一执行命令     *     * @return     */    public V poll() {        return tasksQueue.poll();    }    /**     * 增加执行指令     *     * @param     * @return     */    public boolean add(V value) {        return tasksQueue.add(value);    }    /**     * 清理     */    public void clear() {        tasksQueue.clear();    }    /**     * 获取指令数量     *     * @return     */    public int size() {        return tasksQueue.size();    }    public boolean isComple() {        return comple;    }    public void setComple(boolean comple) {        this.comple = comple;    }}